public class pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int n =6;
		int j;
		for(i=1;i<=n;i++) {
			for(j=1;j<=i;j++) {
			System.out.print("*");
			}
			System.out.println();
		}

	}

}
/* Output
  *
  * *
  * * *
  * * * * 
  * * * * *
  * * * * * *
*/